import React, {Component} from 'react';

class FetchText extends Component{
  constructor()
  {
    super();
    this.state={
      data: [],

      originalSentence:[],
      convertedSentence:[],


    }
  }
  componentDidMount()
  {
    fetch('http://localhost:8000/getSentence')
    .then((response) => response.text())
    .then((findresponse)=>{
      console.log(findresponse) /* this title is a field in the json file*/
      this.setState({
        data:findresponse,
      })
    })
  }
  render()
  {
    return(
        <div>
          <p> {this.state.data} </p>
        </div>
    )
  }

}

export default FetchText;
