import React, {Component} from 'react';

class FetchText extends Component{
  constructor()
  {
    super();
    this.state={
      data: [],

      originalSen:[],
      convertedSen:[],


    }
  }
  componentDidMount()
  {
    fetch('http://localhost:8000/getSentence')
    .then((response) => response.text())
    .then((findresponse)=>{
      console.log(findresponse) /* this title is a field in the json file*/
      this.setState({
        data:findresponse,
        originalSen:findresponse.originalSentence,
        convertedSen:findresponse.convertedSentence,

      })
    })
  }
  render()
  {
    return(
        <div>
          <p> {this.state.data} </p>
        </div>
    )
  }

}

export default FetchText;
