import React, {Component} from 'react';

class FetchText extends Component{
  constructor()
  {
    super();
    this.state={
      data: [],
    }
  }
  componentDidMount()
  {
    fetch('http://localhost:8000/sentence')
    .then((response) => response.json())
    .then((findresponse)=>{
      console.log(findresponse.todos.sentence.title) /* this title is a field in the json file*/
      this.setState({
        data:findresponse.todos.sentence.title,
      })
    })
  }
  render()
  {
    return(
        <div>
          <p> {this.state.data} </p>
        </div>
    )
  }

}

export default FetchText;
